+        <Route path="/auth" element={<AuthPage />} />
+        <Route path="/shop/create/template" element={<LiveShopCreation />} />
+        <Route path="/shop/:userId" element={<ShopPage />} />
+
+        {/* Protected Routes */}
+        <Route path="/shop/dashboard" element={
+          <ProtectedRoute>
+            <ShopDashboard />
+          </ProtectedRoute>
+        } />
+        
+        <Route path="/verify-email" element={
+          <ProtectedRoute requireVerification={false}>
+            <VerifyEmail />
+          </ProtectedRoute>
+        } />
+
+        <Route path="/profile" element={
+          <ProtectedRoute>
+            <ProfilePage />
+          </ProtectedRoute>
+        } />
+
+        <Route path="/notifications" element={
+          <ProtectedRoute>
+            <NotificationsPage />
+          </ProtectedRoute>
+        } />
+      </Routes>
+    </>
+  );
+};
+
+function App() {
+  return (
+    <AuthProvider>
+      <TempStoreProvider>
+        <GlobalStyles />
+          <Router>
+            <AppRoutes />
+          </Router>
+      </TempStoreProvider>
+    </AuthProvider>
+  );
+}
+
+export default App;
