// src/App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import { TempStoreProvider } from './contexts/TempStoreContext';
import ProtectedRoute from './components/auth/ProtectedRoute';

// Pages
import WelcomePage from './pages/WelcomePage';
import ShopPage from './pages/shop/ShopPage';
import LiveShopCreation from './pages/shop/LiveShopCreation';
import AuthPage from './pages/auth/AuthPage';
import VerifyEmail from './pages/auth/VerifyEmail';
import ShopDashboard from './pages/shop/ShopDashboard';

function App() {
  return (
    <AuthProvider>
      <TempStoreProvider>
        <Router>
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<WelcomePage />} />
            <Route path="/auth" element={<AuthPage />} />
            <Route path="/shop/create/template" element={<LiveShopCreation />} />

            {/* Shop routes */}
            <Route path="/shop/:userId" element={<ShopPage />} />

            {/* Protected Routes */}
            <Route path="/shop/dashboard" element={
              <ProtectedRoute>
                <ShopDashboard />
              </ProtectedRoute>
            } />

            <Route path="/verify-email" element={
              <ProtectedRoute requireVerification={false}>
                <VerifyEmail />
              </ProtectedRoute>
            } />

            <Route path="/dashboard" element={
              <ProtectedRoute>
                <ShopDashboard />
              </ProtectedRoute>
            } />

            <Route path="/profile" element={
              <ProtectedRoute>
                <ProfilePage />
              </ProtectedRoute>
            } />

            <Route path="/notifications" element={
              <ProtectedRoute>
                <NotificationsPage />
              </ProtectedRoute>
            } />

            <Route path="/settings" element={
              <ProtectedRoute>
                <SettingsPage />
              </ProtectedRoute>
            } />
          </Routes>
        </Router>
      </TempStoreProvider>
    </AuthProvider>
  );
}

export default App;
