// src/pages/WelcomePage.js

import React, { useState } from 'react';
import styled from 'styled-components';
import { Package, Users } from 'lucide-react';
import { useNavigate } from 'react-router-dom'; // New import

const PageContainer = styled.div.attrs({ className: 'page-container' })`
  min-height: 100vh;
  background: #000000;
  color: #FFFFFF;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: 
      radial-gradient(circle at 20% 30%, rgba(128, 0, 0, 0.2) 0%, transparent 50%),
      radial-gradient(circle at 80% 70%, rgba(128, 0, 0, 0.15) 0%, transparent 50%);
    opacity: 0.8;
    animation: galaxySwirl 30s linear infinite;
  }

  &::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: 
      radial-gradient(circle 1px, #FFF 1px, transparent 1px),
      radial-gradient(circle 2px, #800000 1px, transparent 2px);
    background-size: 200px 200px, 300px 300px;
    background-position: 0 0;
    opacity: 0.1;
    animation: twinkle 4s infinite alternate;
  }

  .ping {
    position: absolute;
    width: 2px;
    height: 2px;
    border-radius: 50%;
    background: #800000;
    pointer-events: none;
  }

  .ping::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 100px;
    height: 100px;
    transform: translate(-50%, -50%);
    border-radius: 50%;
    background: radial-gradient(circle, rgba(128, 0, 0, 0.4) 0%, transparent 70%);
    animation: ping 2s ease-out forwards;
  }

  @keyframes galaxySwirl {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  @keyframes twinkle {
    0%, 100% { opacity: 0.05; }
    50% { opacity: 0.1; }
  }

  @keyframes ping {
    0% {
      width: 0px;
      height: 0px;
      opacity: 1;
    }
    100% {
      width: 200px;
      height: 200px;
      opacity: 0;
    }
  }
`;

const DiscoverButton = styled(ActionButton)`
  background: transparent;
  border: 2px solid #800000;
  color: #800000;

  &:hover {
    background: rgba(128, 0, 0, 0.1);
  }
`;

const Header = styled.header`
  width: 100%;
  height: 80px; // Fixed height
  padding: 0 2rem; // Changed padding
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(0, 0, 0, 0.9);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(128, 0, 0, 0.3);
  position: fixed; // Changed from sticky
  top: 0;
  z-index: 10;
`;

const Logo = styled.div`
  color: #800000;
  font-family: 'Impact', 'Arial Black', sans-serif;
  font-size: 2rem;
  letter-spacing: 2px;
  transform: skew(-5deg);
`;

const MainContent = styled.main`
  max-width: 1200px;
  margin: 0 auto;
  padding: 6rem 2rem 2rem 2rem; // Added top padding
  position: relative;
  z-index: 1;
`;

const LoginButton = styled.button`
  background: transparent;
  border: 2px solid #800000;
  color: #800000;
  padding: 0.8rem 2rem;
  border-radius: 30px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s;
  letter-spacing: 1px;
  font-size: 1rem;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  &:hover {
    background: rgba(128, 0, 0, 0.1);
    transform: translateY(-2px);
    box-shadow: 0 2px 10px rgba(128, 0, 0, 0.2);
  }
`;

const WelcomeSection = styled.section`
  text-align: center;
  margin: 4rem 0;
  position: relative;

  h1 {
    font-family: 'Impact', sans-serif;
    font-size: 4.5rem;
    margin-bottom: 1rem;
    background: linear-gradient(45deg, #800000, #4A0404);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    text-shadow: 0 0 30px rgba(128, 0, 0, 0.3);
    letter-spacing: 2px;
    transform: skew(-5deg);
  }

  p {
    font-size: 1.2rem;
    line-height: 1.6;
    max-width: 800px;
    margin: 0 auto;
    color: rgba(255, 255, 255, 0.8);
    font-weight: 300;
  }
`;

const ActionButton = styled.button`
  background: linear-gradient(45deg, #800000, #4A0404);
  border: none;
  padding: 1rem 2.5rem;
  border-radius: 30px;
  color: white;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.3s;
  text-transform: uppercase;
  letter-spacing: 2px;
  margin-top: 2rem;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(128, 0, 0, 0.3);
  }
`;

const TabContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin: 3rem 0;
`;

const Tab = styled.button`
  background: ${props => props.active ? 'rgba(128, 0, 0, 0.2)' : 'transparent'};
  border: 1px solid ${props => props.active ? '#800000' : 'rgba(128, 0, 0, 0.3)'};
  color: ${props => props.active ? '#FFFFFF' : 'rgba(255, 255, 255, 0.6)'};
  padding: 0.8rem 1.5rem;
  border-radius: 20px;
  cursor: pointer;
  transition: all 0.3s;
  text-transform: uppercase;
  letter-spacing: 1px;
  font-weight: 500;
  font-family: 'Impact', sans-serif;

  &:hover {
    background: rgba(128, 0, 0, 0.2);
    border-color: #800000;
    color: #FFFFFF;
  }
`;

const GridContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
  margin-top: 2rem;
`;

const WelcomePage = () => {
  const navigate = useNavigate(); // New
  const [activeTab, setActiveTab] = useState('featured');
  const handleOpenShop = () => {
    navigate('/shop/create/template');
  };

  const handleLogin = () => {
    navigate('/auth', { 
      state: { 
        mode: 'login',
        from: window.location.pathname
      }
    });
  };

  React.useEffect(() => {
    const container = document.querySelector('.page-container');
    if (!container) return; // Add this check

    const createPing = () => {
      const ping = document.createElement('div');
      ping.className = 'ping';

      ping.style.left = `${Math.random() * 100}%`;
      ping.style.top = `${Math.random() * 100}%`;

      container.appendChild(ping);

      setTimeout(() => {
        if (ping && ping.parentNode) { // Add this check
          ping.remove();
        }
      }, 2000);
    };

    const createPingGroup = (count) => {
      for (let i = 0; i < count; i++) {
        setTimeout(() => {
          createPing();
        }, i * 200);
      }
    };

    const pingCounts = [10, 30, 20];
    let currentIndex = 0;

    const interval = setInterval(() => {
      const count = pingCounts[currentIndex];
      createPingGroup(count);
      currentIndex = (currentIndex + 1) % pingCounts.length;
    }, 3000);

    // Cleanup function
    return () => {
      clearInterval(interval);
      // Remove any remaining pings
      const pings = container.getElementsByClassName('ping');
      while (pings.length > 0) {
        pings[0].remove();
      }
    };
  }, []); // Empty dependency array

  return (
    <PageContainer className="page-container">
      <Header>
        <Logo onClick={() => navigate('/')}>KALKODE</Logo>
        <LoginButton onClick={handleLogin}>
          Login
        </LoginButton>
      </Header>

      <MainContent>
        <WelcomeSection>
          <h1>Welcome to KalKode</h1>
          <p>Join the underground marketplace where local creators thrive. </p>
          <p>Build your empire and discover unique treasures.</p>
          <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginTop: '2rem' }}>
            <ActionButton onClick={handleOpenShop}>Open Up Shop</ActionButton>
            <ActionButton onClick={handleOpenShop}>Open Up Shop</ActionButton>
            <DiscoverButton onClick={() => navigate('/discover')}>
              Discover Shops
            </DiscoverButton>
          </div>
        </WelcomeSection>

        <TabContainer>
          <Tab 
            active={activeTab === 'featured'} 
            onClick={() => setActiveTab('featured')}
          >
            Featured Items
          </Tab>
          <Tab 
            active={activeTab === 'social'} 
            onClick={() => setActiveTab('social')}
          >
            Community
          </Tab>
        </TabContainer>

        <GridContainer>
          {/* Content will be added later */}
        </GridContainer>
      </MainContent>
    </PageContainer>
  );
};

export default WelcomePage;
