// src\pages\shop\components\NavMenu\index.js

import React from 'react';
import styled from 'styled-components';
import { 
  Settings, 
  User, 
  LayoutDashboard, 
  LogOut,
  Bell
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../../../../firebase/config';

const NavContainer = styled.div`
  position: fixed;
  right: 2rem;
  top: 50%;
  transform: translateY(-50%);
  background: ${props => `${props.theme?.colors?.background || '#000000'}CC`};
  backdrop-filter: blur(10px);
  padding: 1rem;
  border-radius: 12px;
  border: 1px solid ${props => `${props.theme?.colors?.accent || '#800000'}30`};
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  z-index: 100;

  // Add hover expansion
  &:hover {
    .label {
      display: block;
      opacity: 1;
      width: auto;
    }
  }
`;

// Update NavButton to handle active state
const NavButton = styled.button`
  background: ${props => props.active ? 
    `${props.theme?.colors?.accent || '#800000'}15` : 
    'transparent'
  };
  opacity: ${props => props.active ? 1 : 0.7};
  // ... rest of your styles
`;

// Update the NavMenu component
const NavMenu = ({ theme }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const menuItems = [
    {
      icon: LayoutDashboard,
      label: 'Dashboard',
      path: '/dashboard'
    },
    {
      icon: User,
      label: 'Profile',
      path: '/profile'
    },
    {
      icon: Bell,
      label: 'Notifications',
      path: '/notifications'
    },
    {
      icon: Settings,
      label: 'Settings',
      path: '/settings'
    }
  ];

  return (
    <NavContainer theme={theme}>
      {menuItems.map((item) => {
        const Icon = item.icon;
        const isActive = location.pathname === item.path;

        return (
          <NavButton 
            key={item.path}
            theme={theme}
            active={isActive}
            onClick={() => navigate(item.path)}
          >
            <div className="icon-container">
              <Icon size={20} />
            </div>
            <span className="label">{item.label}</span>
          </NavButton>
        );
      })}

      <NavButton theme={theme} onClick={handleLogout}>
        <div className="icon-container">
          <LogOut size={20} />
        </div>
        <span className="label">Logout</span>
      </NavButton>
    </NavContainer>
  );
};

export default NavMenu;
